import { useState } from "react";



const Form = () =>{

    const [form,setForm]=useState({
        name:'',
        dificult:'',
        duration:'',
        season:'',
        })

    const [error,setError]=useState({
        name:'',
        dificult:'',
        duration:'',
        season:'',
    })

    function changeHandler(event){
        const property= event.target.name;
        const value= event.target.value;

        validate({...form, [property]:value});
        setForm({...form, [property]:value});
        //validate(form);
    }

    function validate(form){


        if(/^[a-zA-Z]+(([',. -][a-zA-Z ])?[a-zA-Z]*)*$/.test(form.name)){
            setError({...error, name:''});
        }

        else{
            setError({...error, name: 'Error en nombre.'})
        }

        if(form.name === ''){
            setError({...error, name:''});
        }

        if(/^[0-9]*$/.test(form.dificult)){
            setError({...error, dificult:''});
        }
        else{
            setError({...error, dificult:'Error en duración.'});
        }




}


    return(
            <form action="">
                <div>
                    <label>Name: </label>
                    <input type="text" value={form.name} onChange={changeHandler} name='name' />
                    {error.name && <span> {error.name} </span>}
                </div>
                <div>
                    <label>Dificult: </label>
                    <input type="text" value={form.dificult} onChange={changeHandler} name='dificult' />
                    {error.dificult && <span> {error.dificult} </span>}
                </div>
                <div>
                    <label>Duration: </label>
                    <input type="text" value={form.duration} onChange={changeHandler} name='duration' />

                </div>
                <div>
                    <label>Season: </label>
                    <input type="text" value={form.season} onChange={changeHandler} name='season' />
                </div>
            </form>
    )
}

export default Form;
