import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { filterPopulation, getCountries } from "../../redux/actions";

const FilterPopulation = () =>{
    const dispatch= useDispatch();

    useEffect(()=>{
        dispatch(getCountries());
    },[dispatch]);

    const allCountriess= useSelector(state=> state.countres);

    //Este array va a tener las distintas poblaciones.
    let arrayPopulation=allCountriess.filter(elem=>elem.population);

    arrayPopulation=[...new Set(arrayPopulation)];

    console.log('ArrayPopu: '+arrayPopulation.length);

    function handlerFilterPopulation(event){
        dispatch(filterPopulation(event.target.value));
    }

    return(
        <div>
            <select onChange={(event)=>handlerFilterPopulation(event)} >
                {arrayPopulation.map(elem=>(
                    <option value={elem.population}>{elem.population}</option>
                ))}
            </select>
        </div>
    )

}

export default FilterPopulation;
