import { useDispatch, useSelector } from "react-redux";
import { filterContinent } from "../../redux/actions";


const FilterContienent = () =>{
    const dispatch= useDispatch();
    const x = useSelector(state => state.countriesFija);

    let arrayContinents=x.map(elem=>{
        return elem.continent;
    })
    arrayContinents.push('All');

    //Elimina los duplicados del arreglo.
    arrayContinents=[...new Set(arrayContinents)];

    function handlerFilterContinent(event){
        dispatch(filterContinent(event.target.value));
    }

    return(
        <div>
            <select onChange={handlerFilterContinent}>
                {
                    x.map(elem=>(
                        <option value={elem.continent}>
                            {elem.continent}
                        </option>
                    )

                )

                }
            </select>

        </div>
    )
}

export default FilterContienent;
