import React, { useState } from "react";
import { useSelector } from "react-redux";
import { filterContinents } from "../../redux/actions";


const FilterContienent = ({allCountries}) =>{

    let arrayContinents=allCountries.map(elem=>{
        return elem.continent;
    })
    arrayContinents.push('All');
    //Elimina los duplicados del arreglo.
    arrayContinents=[...new Set(arrayContinents)];

    return(
        <div>
            <select>
                {
                    arrayContinents.map(elem=>(
                        <option>
                            {elem}
                        </option>
                    ))
                }
            </select>
        </div>
    )
}

export default FilterContienent;
