import { useDispatch, useSelector } from "react-redux";
import { filterActivities } from "../../redux/actions";


const FilterTravel = () =>{
    const dispatch= useDispatch();
    const allActivities= useSelector(state=> state.activities);


    function handlerFilterActivitie(event){
        dispatch(filterActivities(event.target.value));
        console.log(event.target.value);
    }

    return(
        <div>
            <select onChange={(event) => handlerFilterActivitie(event)} >

            <option value='Americas'>
                    America
                </option>
                <option value='Europe'>
                    Europa
                </option>
                <option value='Antarctic'>
                    Antartida
                </option>
                <option value='Asia'>
                    Asia
                </option>
                <option value='Africa'>
                    Afirca
                </option>
                <option value='Oceania'>
                    Oceania
                </option>
                <option value='All'>
                    All
                </option>
            </select>
            {allActivities.map(elem=>console.log(elem.name))}
        </div>
    )
}

export default FilterTravel;
