import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getCountriesName,getCountries } from "../../redux/actions";


const SearchBar = () => {
    const dispatch = useDispatch();
    const [search, setSearch] = useState('');
    useEffect(() => {
        dispatch(getCountries());
    }, [dispatch]);

    function changeHandlerSearch(event) {
        setSearch(event.target.value)
    }

    function handlerSubmit(event){
        event.preventDefault();
        dispatch(getCountriesName(search));
        setSearch('');
    }

    return (
        <div>
            <label>Search:</label>
            <input type="text" value={search} onChange={(event)=>changeHandlerSearch(event)} />
            <button type="submit" onSubmit={(event)=>handlerSubmit(event)}>Submit</button>
        </div>
    )
}

export default SearchBar;
