import { useDispatch, useSelector } from "react-redux";
import { filterContinent } from "../../redux/actions";


const FilterContienent = () =>{
    const dispatch= useDispatch();
    const allCountries = useSelector(state => state.countries);

    let arrayContinents=allCountries.map(elem=>{
        return elem.continent;
    })
    arrayContinents.push('All');

    //Elimina los duplicados del arreglo.
    arrayContinents=[...new Set(arrayContinents)];

    function handlerFilterContinent(event){
        dispatch(filterContinent(event.target.value));
    }

    return(
        <div>
            <select onChange={(event)=>handlerFilterContinent(event)}>
                {
                    arrayContinents.map(elem=>(
                        <option value={elem}>
                            {elem}
                        </option>
                    )

                )

                }
            </select>
            {arrayContinents.map(e=>console.log('elem: '+e))}
        </div>
    )
}

export default FilterContienent;
