import { useState } from "react";



const Form = () =>{

    const [form,setForm]=useState({
        name:'',
        dificult:'',
        duration:'',
        season:'',
        })

    const [error,setError]=useState({
        name:'',
        dificult:'',
        duration:'',
        season:'',
    })

    function changeHandler(event){
        const property= event.target.name;
        const value= event.target.value;


        setForm({...form [property]=value});
        validate(form);
    }

    function validate(form){
        /*
        if(/^[a-zA-Z]+(([',. -][a-zA-Z ])?[a-zA-Z]*)*$/.test(form.name)){
            console.log('');
        }
        else{
            console.log('Error');
        }

        if(/^[0-9]*$/.test(form.dificult) || form.dificult===''){
            console.log('');
        }
        else{
            console.log('Error');
        }

        if(/^[0-9]*$/.test(form.duration) || form.duration===''){
            console.log('');
        }
        else{
            console.log('Error');
        }

        */

        switch(form){
            case (!/^[a-zA-Z]+(([',. -][a-zA-Z ])?[a-zA-Z]*)*$/.test(form.name)):
                return
                console.log('');

            default:
                console.log('error');
        }

    }


    return(
            <form action="">
                <div>
                    <label>Name: </label>
                    <input type="text" value={form.name} onChange={changeHandler} name='name' />
                </div>
                <div>
                    <label>Dificult: </label>
                    <input type="text" value={form.dificult} onChange={changeHandler} name='dificult' />
                </div>
                <div>
                    <label>Duration: </label>
                    <input type="text" value={form.duration} onChange={changeHandler} name='duration' />
                </div>
                <div>
                    <label>Season: </label>
                    <input type="text" value={form.season} onChange={changeHandler} name='season' />
                </div>
            </form>
    )
}

export default Form;
