import React, { useState } from "react";
import { useSelector } from "react-redux";
import { getCountryId } from "../../redux/actions";


const FilterContienent = ({allCountries}) =>{
    const [idContinent,setIdContinent]= useState('');


    function handlerSelect(event){
        setIdContinent(event.target.value)
    }

    return(
        <div>
            <select onChange={handlerSelect}>
                {
                    allCountries.map(elem=>(
                        <option value={elem.id}>
                            {elem.continent}
                        </option>
                    ))
                }
            </select>
        </div>
    )
}

export default FilterContienent;
