import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { getCountrieName } from "../../redux/actions";


const SearchBar = () => {
    const dispatch = useDispatch();
    const [search, setSearch] = useState('');

    function changeHandlerSearch(event) {
        setSearch(event.target.value);
    }

    function handlerSubmit(event){
        event.preventDefault();
       console.log( search[0].toUpperCase());
        dispatch(getCountrieName(search));
        setSearch('');
    }

    return (
        <div>
            <label>Search:</label>
            <input type="text" onChange={(event)=>changeHandlerSearch(event)} value={search} />
            <button type="submit" onClick={(event)=>handlerSubmit(event)}>Submit</button>
        </div>
    )
}

export default SearchBar;
